require 'spec_helper'

describe AppConfig do
  it "should now allow 2 app configs to exist" do
    FactoryGirl.create(:app_config)
    ac = AppConfig.new
    ac.valid?.should == false
  end
  it "should be able to have a really long front_page" do
    ac = AppConfig.new
    ac.front_page = ""
    ac.valid?.should == true
    ac.save.should == true
  end
end
