require 'spec_helper'

describe AppConfig, :type => :model do
  it "should now allow 2 app configs to exist" do
    FactoryGirl.create(:app_config)
    ac = AppConfig.new
    expect(ac.valid?).to eq(false)
  end
  it "should be able to have a really long front_page" do
    ac = AppConfig.new
    ac.front_page = ""
    expect(ac.valid?).to eq(true)
    expect(ac.save).to eq(true)
  end
end
